import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from './app/store';
import { setCryptos, updateCryptoPrice } from './features/crypto/cryptoSlice';
import './App.css';

// Helper function to generate mock chart data
const generateMockChartData = () => {
  const points = [];
  let value = 100;
  for (let i = 0; i < 24; i++) {
    value = value * (1 + (Math.random() - 0.5) * 0.1);
    points.push(value);
  }
  return points;
};

// Helper function to create SVG sparkline
const createSparkline = (data: number[], color: string) => {
  const width = 160;
  const height = 50;
  const max = Math.max(...data);
  const min = Math.min(...data);
  const range = max - min;
  
  const points = data
    .map((value, index) => {
      const x = (index / (data.length - 1)) * width;
      const y = height - ((value - min) / range) * height;
      return `${x},${y}`;
    })
    .join(' ');

  return `data:image/svg+xml;utf8,<svg viewBox="0 0 ${width} ${height}" xmlns="http://www.w3.org/2000/svg">
    <polyline
      fill="none"
      stroke="${color}"
      stroke-width="2"
      points="${points}"
    />
  </svg>`;
};

const mockCryptoData = [
  {
    id: 'bitcoin',
    name: 'Bitcoin',
    symbol: 'BTC',
    logo: 'https://assets.coingecko.com/coins/images/1/small/bitcoin.png',
    price: 35000,
    change1h: 0.5,
    change24h: 2.5,
    change7d: -1.2,
    marketCap: 680000000000,
    volume24h: 28500000000,
    circulatingSupply: 19500000,
    maxSupply: 21000000,
    chartData: generateMockChartData()
  },
  {
    id: 'ethereum',
    name: 'Ethereum',
    symbol: 'ETH',
    logo: 'https://assets.coingecko.com/coins/images/279/small/ethereum.png',
    price: 2000,
    change1h: -0.2,
    change24h: 1.8,
    change7d: 3.5,
    marketCap: 240000000000,
    volume24h: 15700000000,
    circulatingSupply: 120000000,
    maxSupply: null,
    chartData: generateMockChartData()
  },
  {
    id: 'tether',
    name: 'Tether',
    symbol: 'USDT',
    logo: 'https://assets.coingecko.com/coins/images/325/small/Tether.png',
    price: 1,
    change1h: 0.1,
    change24h: 0.05,
    change7d: 0.02,
    marketCap: 83000000000,
    volume24h: 32100000000,
    circulatingSupply: 83000000000,
    maxSupply: null,
    chartData: generateMockChartData()
  },
  {
    id: 'binancecoin',
    name: 'BNB',
    symbol: 'BNB',
    logo: 'https://assets.coingecko.com/coins/images/825/small/bnb-icon2_2x.png',
    price: 310,
    change1h: 0.8,
    change24h: -1.2,
    change7d: 2.1,
    marketCap: 47000000000,
    volume24h: 980000000,
    circulatingSupply: 153000000,
    maxSupply: 200000000,
    chartData: generateMockChartData()
  },
  {
    id: 'solana',
    name: 'Solana',
    symbol: 'SOL',
    logo: 'https://assets.coingecko.com/coins/images/4128/small/solana.png',
    price: 98,
    change1h: 1.5,
    change24h: 3.2,
    change7d: 5.8,
    marketCap: 42000000000,
    volume24h: 2100000000,
    circulatingSupply: 410000000,
    maxSupply: null,
    chartData: generateMockChartData()
  }
];

function App() {
  const dispatch = useDispatch();
  const { cryptos } = useSelector((state: RootState) => state.crypto);

  useEffect(() => {
    dispatch(setCryptos(mockCryptoData));

    const interval = setInterval(() => {
      mockCryptoData.forEach(crypto => {
        const randomChange = (Math.random() - 0.5) * 2;
        const newPrice = crypto.price * (1 + randomChange / 100);
        dispatch(updateCryptoPrice({
          id: crypto.id,
          price: newPrice,
          change1h: crypto.change1h + randomChange / 8,
          change24h: crypto.change24h + randomChange / 4,
          change7d: crypto.change7d + randomChange / 2,
        }));
      });
    }, 3000);

    return () => clearInterval(interval);
  }, [dispatch]);

  const formatNumber = (num: number, isCurrency: boolean = false): string => {
    if (num >= 1e9) {
      return `${isCurrency ? '$' : ''}${(num / 1e9).toFixed(2)}B`;
    }
    if (num >= 1e6) {
      return `${isCurrency ? '$' : ''}${(num / 1e6).toFixed(2)}M`;
    }
    return `${isCurrency ? '$' : ''}${num.toLocaleString(undefined, {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    })}`;
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Crypto Tracker</h1>
      </header>
      <div className="table-container">
        <table className="crypto-table">
          <thead>
            <tr>
              <th>#</th>
              <th style={{ textAlign: 'left' }}>Name</th>
              <th>Symbol</th>
              <th>Price</th>
              <th>1h %</th>
              <th>24h %</th>
              <th>7d %</th>
              <th>Market Cap</th>
              <th>24h Volume</th>
              <th>Circulating Supply</th>
              <th>Max Supply</th>
              <th>7D Chart</th>
            </tr>
          </thead>
          <tbody>
            {cryptos.map((crypto, index) => (
              <tr key={crypto.id}>
                <td>{index + 1}</td>
                <td className="name-cell">
                  <img 
                    src={crypto.logo} 
                    alt={`${crypto.name} logo`} 
                    className="crypto-logo"
                  />
                  <span className="crypto-name">{crypto.name}</span>
                </td>
                <td>{crypto.symbol}</td>
                <td>{formatNumber(crypto.price, true)}</td>
                <td className={`change-cell ${crypto.change1h >= 0 ? 'positive' : 'negative'}`}>
                  {crypto.change1h > 0 ? '+' : ''}{crypto.change1h.toFixed(2)}%
                </td>
                <td className={`change-cell ${crypto.change24h >= 0 ? 'positive' : 'negative'}`}>
                  {crypto.change24h > 0 ? '+' : ''}{crypto.change24h.toFixed(2)}%
                </td>
                <td className={`change-cell ${crypto.change7d >= 0 ? 'positive' : 'negative'}`}>
                  {crypto.change7d > 0 ? '+' : ''}{crypto.change7d.toFixed(2)}%
                </td>
                <td>{formatNumber(crypto.marketCap, true)}</td>
                <td>{formatNumber(crypto.volume24h, true)}</td>
                <td>{formatNumber(crypto.circulatingSupply)} {crypto.symbol}</td>
                <td>{crypto.maxSupply ? formatNumber(crypto.maxSupply) : '∞'}</td>
                <td className="chart-cell">
                  <img 
                    src={createSparkline(crypto.chartData, crypto.change7d >= 0 ? '%234caf50' : '%23f44336')}
                    alt={`${crypto.name} 7-day chart`}
                    className="mini-chart"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
