import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface CryptoData {
  id: string;
  name: string;
  symbol: string;
  logo: string;
  price: number;
  change1h: number;
  change24h: number;
  change7d: number;
  marketCap: number;
  volume24h: number;
  circulatingSupply: number;
  maxSupply: number | null;
  chartData: number[];
}

interface CryptoState {
  cryptos: CryptoData[];
  loading: boolean;
  error: string | null;
}

const initialState: CryptoState = {
  cryptos: [],
  loading: false,
  error: null,
};

export const cryptoSlice = createSlice({
  name: 'crypto',
  initialState,
  reducers: {
    setCryptos: (state, action: PayloadAction<CryptoData[]>) => {
      state.cryptos = action.payload;
      state.loading = false;
      state.error = null;
    },
    setLoading: (state, action: PayloadAction<boolean>) => {
      state.loading = action.payload;
    },
    setError: (state, action: PayloadAction<string>) => {
      state.error = action.payload;
      state.loading = false;
    },
    updateCryptoPrice: (state, action: PayloadAction<{
      id: string;
      price: number;
      change1h: number;
      change24h: number;
      change7d: number;
    }>) => {
      const crypto = state.cryptos.find(c => c.id === action.payload.id);
      if (crypto) {
        crypto.price = action.payload.price;
        crypto.change1h = action.payload.change1h;
        crypto.change24h = action.payload.change24h;
        crypto.change7d = action.payload.change7d;
      }
    },
  },
});

export const { setCryptos, setLoading, setError, updateCryptoPrice } = cryptoSlice.actions;

export default cryptoSlice.reducer;
